"""
https://www.hackerrank.com/challenges/merge-the-tools/problem
"""

def merge_the_tools(s, k):
    divider = int(len(s) / k)
    for i in range(divider):
        base = s[k * i : k * (i+1)]
        temp = ""
        for letter in base:
            if letter not in temp:
                temp += letter
        print(temp)

task_1 = ""
n = 5424

merge_the_tools(task_1, n)


